package methods;

public class printFMethod {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// printf is used to format the output displayed on the console.
		// the first is the string and second is the value/object/variable
		// Need to specify the format using % in the first param that is string
		
		// Example
		System.out.printf("Integer = %d", 123);
		System.out.printf("\tBoolean = %b", true);
		System.out.printf("\tCharacter = %c", '@');
		System.out.printf("\tString = %s", "Gaurav\t");
		System.out.printf("\tFloat = %f", 123.2);
	}

}
